mapper.select:

select

from class_schedule
where account = #{account,jdbcType=VARCHAR}
and week_number = #{weekNumber,jdbcType=CHAR}
and term_range = #{termRange,jdbcType=VARCHAR}
and course_name = #{courseName,jdbcType=VARCHAR}
and place = #{place,jdbcType=VARCHAR}
and course_number = #{courseNumber,jdbcType=VARCHAR}

mapper.delete:

delete from class_schedule
where account = #{account,jdbcType=VARCHAR}
and week_number = #{weekNumber,jdbcType=CHAR}
and term_range = #{termRange,jdbcType=VARCHAR}
and course_name = #{courseName,jdbcType=VARCHAR}
and place = #{place,jdbcType=VARCHAR}
and course_number = #{courseNumber,jdbcType=VARCHAR}

mapper.insert:

insert into class_schedule (account, week_number, term_range,
course_name, place, course_number,
teacher, week_range, week_type,
session_start, session_end, creation_time
)
values (#{account,jdbcType=VARCHAR}, #{weekNumber,jdbcType=CHAR}, #{termRange,jdbcType=VARCHAR},
#{courseName,jdbcType=VARCHAR}, #{place,jdbcType=VARCHAR}, #{courseNumber,jdbcType=VARCHAR},
#{teacher,jdbcType=VARCHAR}, #{weekRange,jdbcType=VARCHAR}, #{weekType,jdbcType=VARCHAR},
#{sessionStart,jdbcType=VARCHAR}, #{sessionEnd,jdbcType=VARCHAR}, #{creationTime,jdbcType=TIMESTAMP}
)

mapper.update:

update class_schedule


        teacher = #{teacher,jdbcType=VARCHAR},


        week_range = #{weekRange,jdbcType=VARCHAR},


        week_type = #{weekType,jdbcType=VARCHAR},


        session_start = #{sessionStart,jdbcType=VARCHAR},


        session_end = #{sessionEnd,jdbcType=VARCHAR},


        creation_time = #{creationTime,jdbcType=TIMESTAMP},


where account = #{account,jdbcType=VARCHAR}
and week_number = #{weekNumber,jdbcType=CHAR}
and term_range = #{termRange,jdbcType=VARCHAR}
and course_name = #{courseName,jdbcType=VARCHAR}
and place = #{place,jdbcType=VARCHAR}
and course_number = #{courseNumber,jdbcType=VARCHAR}


update class_schedule
set teacher = #{teacher,jdbcType=VARCHAR},
week_range = #{weekRange,jdbcType=VARCHAR},
week_type = #{weekType,jdbcType=VARCHAR},
session_start = #{sessionStart,jdbcType=VARCHAR},
