mapper.select:

select 

from empty_classroom
where empty_date = #{emptyDate,jdbcType=TIMESTAMP}
  and empty_time = #{emptyTime,jdbcType=VARCHAR}
  and campus_type = #{campusType,jdbcType=VARCHAR}
  and classroom_building = #{classroomBuilding,jdbcType=VARCHAR}
  and classroom_name = #{classroomName,jdbcType=VARCHAR}

mapper.delete:

delete from empty_classroom
where empty_date = #{emptyDate,jdbcType=TIMESTAMP}
  and empty_time = #{emptyTime,jdbcType=VARCHAR}
  and campus_type = #{campusType,jdbcType=VARCHAR}
  and classroom_building = #{classroomBuilding,jdbcType=VARCHAR}
  and classroom_name = #{classroomName,jdbcType=VARCHAR}

mapper.update:

update empty_classroom


    classroom_number = #{classroomNumber,jdbcType=VARCHAR},


    classroom_capacity = #{classroomCapacity,jdbcType=VARCHAR},


    creation_time = #{creationTime,jdbcType=TIMESTAMP},


where empty_date = #{emptyDate,jdbcType=TIMESTAMP}
  and empty_time = #{emptyTime,jdbcType=VARCHAR}
  and campus_type = #{campusType,jdbcType=VARCHAR}
  and classroom_building = #{classroomBuilding,jdbcType=VARCHAR}
  and classroom_name = #{classroomName,jdbcType=VARCHAR}


update empty_classroom
set classroom_number = #{classroomNumber,jdbcType=VARCHAR},
  classroom_capacity = #{classroomCapacity,jdbcType=VARCHAR},
  creation_time = #{creationTime,jdbcType=TIMESTAMP}
where empty_date = #{emptyDate,jdbcType=TIMESTAMP}
  and empty_time = #{emptyTime,jdbcType=VARCHAR}
  and campus_type = #{campusType,jdbcType=VARCHAR}
  and classroom_building = #{classroomBuilding,jdbcType=VARCHAR}
  and classroom_name = #{classroomName,jdbcType=VARCHAR}

mapper.select:
select * from empty_classroom


    empty_date=#{emptyDate,jdbcType=TIMESTAMP}


    and empty_Time= #{emptyTime,jdbcType=VARCHAR}


    and classroom_building= #{classroomBuilding,jdbcType=VARCHAR}


order by classroom_name asc;
