mapper.select:

select 

from score_data
where account = #{account,jdbcType=VARCHAR}
  and term_range = #{termRange,jdbcType=VARCHAR}
  and course_name = #{courseName,jdbcType=VARCHAR}
  and course_type = #{courseType,jdbcType=VARCHAR}

mapper.delete:

delete from score_data
where account = #{account,jdbcType=VARCHAR}
  and term_range = #{termRange,jdbcType=VARCHAR}
  and course_name = #{courseName,jdbcType=VARCHAR}
  and course_type = #{courseType,jdbcType=VARCHAR}

mapper.update:

update score_data


    name = #{name,jdbcType=VARCHAR},


    exam_type = #{examType,jdbcType=VARCHAR},


    score = #{score,jdbcType=VARCHAR},


    hour_credit = #{hourCredit,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


    creation_time = #{creationTime,jdbcType=TIMESTAMP},


where account = #{account,jdbcType=VARCHAR}
  and term_range = #{termRange,jdbcType=VARCHAR}
  and course_name = #{courseName,jdbcType=VARCHAR}
  and course_type = #{courseType,jdbcType=VARCHAR}


update score_data
set name = #{name,jdbcType=VARCHAR},
  exam_type = #{examType,jdbcType=VARCHAR},
  score = #{score,jdbcType=VARCHAR},
  hour_credit = #{hourCredit,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  creation_time = #{creationTime,jdbcType=TIMESTAMP}
where account = #{account,jdbcType=VARCHAR}
  and term_range = #{termRange,jdbcType=VARCHAR}
  and course_name = #{courseName,jdbcType=VARCHAR}
  and course_type = #{courseType,jdbcType=VARCHAR}

mapper.insert:
replace into score_data (
account, term_range, course_name,
